sources.scss/components/_buttons.scss:
@use "src/scss/abstracts/colors" as *;
@use "src/scss/abstracts/type" as *;
@use "src/scss/base/font-face" as *;
@use "src/scss/helpers/variables" as *;

// CTA Button Styles
.primary-btn,
.secondary-btn,
.accent-btn {
  border-style: none;
  padding: 1em;
  border-radius: 8px;
  font-weight: $fw-n;
}
.primary-btn,
.secondary-btn {
  box-shadow: $ds;
  margin: 0 0.5em;
}
.primary-btn {
  background-color: var(--clr-primary-100);
  &:hover {
    background-color: white;
    color: var(--clr-primary-100);
    border-color: var(--clr-primary-100);
    border-style: solid;
    border-width: 4px;
    padding: calc(1em - 4px);
  }
}
.secondary-btn {
  color: var(--clr-primary-500);
  background-color: var(--clr-neutrals-light);
  &:hover {
    background-color: white;
    color: var(--clr-primary-500);
    border-color: var(--clr-neutrals-400);
    border-style: solid;
    border-width: 4px;
    padding: calc(1em - 4px);
  }
}
.accent-btn {
  background-color: var(--clr-accent-500);
  &:hover {
    background-color: white;
    color: var(--clr-accent-500);
    border-color: var(--clr-accent-500);
    border-style: solid;
    border-width: 4px;
    padding: calc(1em - 4px);
  }
}

//Feature Tabs Navigation Style

// Accordian FAQ Button
.accordian {
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  background-color: white;
  border-style: none;
  padding: 0;
  width: 100%;
  h3 {
    font-size: 1.4rem;
    margin: 0;
    margin-bottom: 0.5em;
  }
}
.icon-arrow {
  height: 100%;
  width: 30px;
}
.panel {
  &::after {
    content: "";
    background-color: var(--clr-neutrals-light);
    display: inline-block;
    height: 2px;
    width: 100%;
    margin-bottom: 3em;
  }
}

sources.scss/layout/_hero.scss:
@use "src/scss/abstracts/colors" as *;
@use "src/scss/abstracts/mixins" as *;

.hero h1 {
  @include mq {
    font-size: 3vw;
    text-align: left;
    margin-top: 0;
  }
  @include mq(90em) {
    font-size: 48px;
  }
}

.hero-img-container {
  position: relative;
  width: 100%;
  display: flex;
  flex-direction: row;
  justify-items: center;
  @include mq {
    width: 60%;
    min-width: 520px;
  }
}
.hero-img {
  margin: auto;
}
.feature-img {
  padding: 3em;
  width: 90%;
  max-width: 900px;
  margin-left: auto;
}
.decorative-block {
  position: absolute;
  bottom: 0;
  right: 0;
  height: 75%;
  width: 75%;

  background-color: var(--clr-primary-100);
  z-index: -1;
  border-radius: 200px 0 0 200px;
}

.button-group {
  width: 100%;
  display: flex;
  flex-direction: row;
  justify-content: center;
  @include mq {
    justify-content: left;
    button {
      margin-right: 1;
    }
  }
}

// Media Queries
.hero {
  @include mq {
    display: flex;
    flex-direction: row-reverse;
    text-align: left;
    justify-content: space-between;
    padding-left: 3em;
    .heading-wrapper {
      align-self: center;
      margin-left: auto;
      width: 40%;
      max-width: 530px;

      p {
        max-width: 50vw;
      }
      button {
        font-size: 0.8rem;
      }
    }
  }
}

.menu-btn {
  background-color: transparent;
  border-style: none;
}
.menu-wrapper {
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  width: 80%;
  margin: 3em auto;
}

sources.scss/layout/_features.scss:
@use "src/scss/base/font-face" as *;
@use "src/scss/abstracts/mixins" as *;

.feature-img-container {
  position: relative;
  width: 100%;
  @include mq {
    min-width: 520px;
    display: flex;
    flex-direction: row;
    align-items: flex-end;
  }
}
.feature-img-container {
  .decorative-block {
    left: 0;
    border-radius: 0 200px 200px 0;
  }
}
.feature-nav {
  display: flex;
  flex-direction: column;
  align-content: center;
  margin-bottom: 3em;
  @include mq {
    width: 40em;
    flex-direction: row;
    justify-content: space-between;
  }
}
.feature-tab {
  padding: 0;
  background-color: white;
  border-style: none;
  display: flex;
  flex-direction: column;
  justify-content: center;
  &::after {
    content: "";
    height: 2px;
    width: 100%;
    background-color: var(--clr-neutrals-light);
  }
  @include mq {
    width: 100%;
  }
}
.tab-highlight {
  background-color: transparent;
  height: 4px;
  width: 50%;
  max-width: 250px;
  margin: auto;
  @include mq {
    width: 100%;
  }
}
.tab-heading {
  color: var(--clr-neutrals-100);
  font-weight: $fw-n;
  &:hover {
    color: var(--clr-accent-500);
  }
  @include mq {
    font-size: 1.2rem;
  }
}
.active {
  .tab-heading {
    color: var(--clr-primary-500);
    font-weight: $fw-b;
  }
  .tab-highlight {
    background-color: var(--clr-accent-500);
  }
}

//Feature Item Content
.feature-item {
  display: none;
  flex-direction: column;
  align-items: center;
  margin-bottom: 3em;
  @include mq {
    display: none;
    flex-direction: row;
  }
}
.active {
  display: flex;
}

.features {
  display: flex;
  flex-direction: column;
  align-items: center;
  .title {
    width: 80%;
    max-width: 640px;
  }
}

.feature-heading-wrapper {
  display: flex;
  flex-direction: column;
  align-items: center;
  @include mq {
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    margin-right: 3em;
    h2 {
      text-align: left;
      width: 80%;
      margin-top: 2em;
      margin-bottom: 0;
    }
    .primary-btn {
      width: auto;
      margin: 0;
    }
  }
}

.feature-items {
  width: 100%;
}

