sources.scss/layout/_newsletter.scss:
@use "src/scss/helpers/variables" as *;
@use "src/scss/abstracts/colors" as *;
@use "src/scss/base/font-face" as *;
@use "src/scss/abstracts/mixins" as *;

.newsletter {
  background-color: var(--clr-primary-100);
  padding-top: 8em;
  padding-bottom: 4em;
  margin-bottom: 0;
  display: flex;
  flex-direction: column;
  align-items: center;
  h2 {
    color: white;
    margin: 0;
  }
}
.container {
  @include mq {
    width: 60%;
  }
}

.heading-wrapper {
  margin-bottom: 3em;
}

.small-h2 {
  font-weight: $fw-n;
  font-size: 0.8rem;
  letter-spacing: 4px;
  text-transform: uppercase;
}

.input-wrapper {
  display: flex;
  flex-direction: column;
  position: relative;
  z-index: 0;
  padding: 6px;
  align-items: center;
  .label-error {
    position: absolute;
    left: 12px;
    right: 12px;
    top: 12px;
  }
  @include mq {
    flex-direction: row;
    justify-items: center;
    .accent-btn {
      margin: 0 0 0 1em;
      width: 40%;
    }
  }
}

.error {
  display: none;
  flex-direction: column;
  justify-content: flex-end;
  left: 0;
  right: 0;
  top: 0;
  width: 100%;
  height: 9em;
  z-index: -1;
  background-color: var(--clr-accent-500);
  padding: 0.5em;
  border-radius: 8px;

  &::after {
    content: "Whoops, make sure it's an email";
    font-size: 0.8rem;
    color: white;
    font-style: italic;
  }
}

.active {
  display: flex;
  position: relative;
}

.label-normal {
  position: relative;
  width: 100%;
  margin: 0;
}

#email {
  font-size: 1rem;
  height: 50px;
  width: 100%;
  padding: 1em;
  border-radius: 0.5em;
  border-style: none;
}

.contact-us {
  width: 100%;
  margin-top: 1em;
}

sources.scss/layout/_nav.scss:
@use "src/scss/helpers/variables" as *;
@use "src/scss/abstracts/colors" as *;
@use "src/scss/base/font-face" as *;
@use "src/scss/abstracts/mixins" as *;

.desktop,
.nav-links {
  display: flex;
  flex-direction: column;
  align-items: center;
  @include mq {
    flex-direction: row;
    padding: 0 3em;
  }
}
.desktop {
  background-color: var(--clr-primary-500);
  margin-bottom: 0;
  @include mq {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
  }
  .logo {
    margin: 3em 0;
    width: 35%;
    max-width: 240px;
    @include mq {
      margin-right: 3em;
    }
  }
  .nav-links a {
    margin: 1em 0;
    text-transform: uppercase;
    letter-spacing: 3px;
    font-weight: $fw-n;
    &:hover {
      color: var(--clr-accent-500);
    }
    @include mq {
      margin: 0 1em;
    }
  }
}
.social-links {
  display: flex;
  flex: row;
  align-items: center;
  margin: 3em 0;
  .social-link {
    margin: 2em;
    width: 34px;
  }
  @include mq {
    align-self: flex-end;
    .social-link {
      margin: 1em;
    }
  }
}

.mobile {
  display: flex;
  width: 100vw;
  height: 100vh;
  flex-direction: column;
  background-color: var(--clr-primary-nav);
  justify-content: space-around;
  ul,
  li {
    list-style: none;
  }
  ul {
    display: flex;
    flex-direction: column;
    align-items: center;
    height: 100%;
    justify-content: space-around;
    .menu {
      display: flex;
      flex-direction: row;
      width: 100%;
      align-items: center;
      justify-content: space-around;
    }
    li a {
      font-size: 3.2em;
    }
  }
  @include mq {
    display: none;
  }
}
.top {
  display: none;

  @include mq {
    display: flex;
    background-color: white;
    .nav-container .nav-links a {
      color: var(--clr-primary-500);
      &:hover {
        color: var(--clr-accent-500);
      }
    }
    .logo {
      min-width: 160px;
    }
  }
}

.nav-container {
  @include mq {
    display: flex;
    flex-direction: row;
    width: 100%;
    justify-content: center;
    .accent-btn {
      align-self: center;
    }
  }
}

.menu-wrapper {
  @include mq {
    display: none;
  }
}
